package com.blossoming.maker.Fragment;

import android.app.Activity;
import android.app.Fragment;
import android.content.Context;
import android.os.AsyncTask;
import android.os.Build;
import android.os.Bundle;
import android.os.Handler;
import android.os.Message;
import android.support.annotation.Nullable;
import android.support.v4.view.ViewPager;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ImageView;
import android.widget.LinearLayout;
import android.widget.ListView;

import com.blossoming.maker.Model.EventsInfo;
import com.blossoming.maker.Model.ItemEvents;
import com.blossoming.schoolradio.R;
import com.facebook.drawee.backends.pipeline.Fresco;

import java.util.ArrayList;
import java.util.List;
import java.util.Timer;
import java.util.TimerTask;

/**
 * Created by Blossoming on 2016/9/9.
 */
public class DefaultFragment extends Fragment {
    private static final String ARTICAL_LATEST_PARAM = "param";
    private static final int UPDATE_VIEWPAGER = 0;
    private ViewPager vpHottest;
    private LinearLayout llHottestIndicator;
    //listview部分
    private ListView lv_maker_events;
    private List<EventsInfo> lists=new ArrayList<EventsInfo>();
    private EventsAdapter adapter;


    private String mParam;
    private Activity mAct;

    private int autoCurrIndex = 0;
    private ImageView[] mBottomImages;
    private Timer timer = new Timer();

    private Handler mHandler = new Handler() {
        public void handleMessage(Message msg) {
            switch (msg.what) {
                case UPDATE_VIEWPAGER:
                    if (msg.arg1 != 0) {
                        vpHottest.setCurrentItem(msg.arg1);
                    } else {
                        vpHottest.setCurrentItem(msg.arg1, false);
                    }
                    break;
            }
        }
    };

    public static DefaultFragment newInstance(String param) {
        DefaultFragment fragment = new DefaultFragment();
        Bundle args = new Bundle();
        args.putString(ARTICAL_LATEST_PARAM,param);
        fragment.setArguments(args);
        return fragment;
    }
    //初始化数据


    /*
     * Deprecated on API 23
     * Use onAttachToContext instead
     */
    @SuppressWarnings("deprecation")
    @Override
    public void onAttach(Activity activity) {
        super.onAttach(activity);
        if (Build.VERSION.SDK_INT < Build.VERSION_CODES.M) {
            onAttachToContext(activity);
            for(int i=0;i<10;i++) {
                EventsInfo eventsInfo = new EventsInfo(R.drawable.event01, "信息工程学院计算机专业2006届毕业生十年再相聚，返校致青春");
                lists.add(eventsInfo);
            }
            adapter=new EventsAdapter(activity,R.layout.lv_item_defaultfg,lists);
        }
    }

    /*
     * Called when the fragment attaches to the context
     */
    protected void onAttachToContext(Context context) {
        //do something
    }


    @Override
    public void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        //Fresco.initialize(mAct);
        if(savedInstanceState!=null)
        {
            mParam=savedInstanceState.getString(ARTICAL_LATEST_PARAM);
        }
    }
    //加载视图
    @Nullable
    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container, Bundle savedInstanceState) {
        View view = inflater.inflate(R.layout.fg_default, container, false);
        mAct=getActivity();
        lv_maker_events= (ListView) view.findViewById(R.id.lv_maker_events);

        lv_maker_events.setAdapter(adapter);
        return view;
    }

    @Override
    public void onActivityCreated(Bundle savedInstanceState) {
        super.onActivityCreated(savedInstanceState);

        new ImageTask().execute();
    }

    @Override
    public void onDestroy() {
        super.onDestroy();
    }





    //下面是ViewPager加载图片
    private void setUpViewPager(final List<ItemEvents> events)
    {
        Fresco.initialize(mAct);
        HeaderAdapter imageAdapter=new HeaderAdapter(mAct,events);
        vpHottest= (ViewPager) mAct.findViewById(R.id.vp_hottest);
        vpHottest.setAdapter(imageAdapter);

        mBottomImages=new ImageView[events.size()];
        for(int i=0;i<mBottomImages.length;i++)
        {
            ImageView imageView=new ImageView(mAct);
            LinearLayout.LayoutParams params=new LinearLayout.LayoutParams(10,10);
            params.setMargins(5,0,5,0);
            imageView.setLayoutParams(params);
            if(i==0)
            {
                imageView.setBackgroundResource(R.drawable.radiobutton_on_background);
            }
            else
            {
                imageView.setBackgroundResource(R.drawable.radiobutton_off_background);
            }
            mBottomImages[i]=imageView;
            llHottestIndicator= (LinearLayout) mAct.findViewById(R.id.ll_hottest_indicator);
            llHottestIndicator.addView(mBottomImages[i]);
        }
        vpHottest.addOnPageChangeListener(new ViewPager.OnPageChangeListener() {
            @Override
            public void onPageScrolled(int position, float positionOffset, int positionOffsetPixels) {

            }

            @Override
            public void onPageSelected(int position) {
                int total=mBottomImages.length;
                for(int j=0;j<total;j++)
                {
                    if(j==position)
                    {
                        mBottomImages[j].setBackgroundResource(R.drawable.radiobutton_on_background);

                    }
                    else
                    {
                        mBottomImages[j].setBackgroundResource(R.drawable.radiobutton_off_background);
                    }
                    autoCurrIndex=position;
                }
            }

            @Override
            public void onPageScrollStateChanged(int state) {

            }
        });
        timer.schedule(new TimerTask() {
            @Override
            public void run() {
                Message message = new Message();
                message.what = UPDATE_VIEWPAGER;
                if (autoCurrIndex == events.size() - 1) {
                    autoCurrIndex = -1;
                }
                message.arg1 = autoCurrIndex + 1;
                mHandler.sendMessage(message);
            }
        },5000,5000);
    }
    class ImageTask extends AsyncTask<String, Void, List<ItemEvents>> {
        @Override
        protected List<ItemEvents> doInBackground(String... params) {
            List<ItemEvents> articles = new ArrayList<ItemEvents>();
            articles.add(
                    new ItemEvents(1123, "data:image/jpeg;base64,"));
            articles.add(
                    new ItemEvents(1123, "data:image/jpeg;base64,"));
            articles.add(
                    new ItemEvents(1123, "http://img1.imgtn.bdimg.com/it/u=940331630,3837033723&fm=21&gp=0.jpg"));
            articles.add(
                    new ItemEvents(1123, "http://img0.imgtn.bdimg.com/it/u=3917819831,3480049187&fm=21&gp=0.jpg"));
            return articles;
        }

        @Override
        protected void onPostExecute(List<ItemEvents> events) {
            //这儿的 是 url 的集合
            super.onPostExecute(events);
            setUpViewPager(events);

        }
    }
}
